mapper.select:
SELECT qna_no, member_id, member_nickname, qna_category, qna_title, qna_secret, create_date, update_date FROM qna
qna_title LIKE CONCAT ('%',#{searchWord},'%') OR qna_category LIKE CONCAT('%',#{searchWord},'%')
ORDER BY update_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT qna_no, member_id, member_nickname, qna_category, qna_title, qna_content, qna_secret, create_date, update_date FROM qna ORDER BY update_date DESC
qna_no NOT IN (SELECT qna_no FROM qna_answer)
qna_category = #{qnaCategory}
LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) FROM qna_image
WHERE
	qna_no = #{qnaNo}
