mapper.update:
UPDATE qna SET
	qna_category = #{qnaCategory},
	qna_title = #{qnaTitle},
	qna_content = #{qnaContent},
	update_date = NOW()
WHERE qna_no = #{qnaNo}
AND member_id = #{memberId}
AND member_nickname = #{memberNickname}

mapper.delete:
DELETE FROM qna 
WHERE qna_no = #{qnaNo}
AND member_nickname = #{memberNickname}

DELETE FROM qna_image(
	WHERE qna_image_no = #{qnaImgNo} 
	AND qna_no = #{qnaNo}
)

mapper.select:
SELECT qna_no, member_id, member_nickname, qna_category, qna_title, qna_content, qna_secret, create_date, update_date FROM qna ORDER BY update_date DESC
qna_no NOT IN (SELECT qna_no FROM qna_answer)
qna_category = #{qnaCategory}
LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) FROM qna

	
		searchWord = #{searchWord}
	
	AND
	
		qnaCategory = #{qnaCategory}
